hypre__i_3 = hypre__i * NALU_HYPRE_XCONCAT3(hypre__stride,j,3) + NALU_HYPRE_XCONCAT3(hypre__box_start_imin,j,3);\
/* 3 times */ \
i1 += hypre__i_1 * hypre__I_1; \
