hypre__I_3 *= NALU_HYPRE_XCONCAT3(hypre__box_imax_imin,j,3); \
/* */ \
hypre__J /= NALU_HYPRE_XCONCAT2(hypre__loop_size,j); \
/* save the 3-D id */ \
NALU_HYPRE_XCONCAT2(hypre__id,j) = hypre__i;


